import {useState} from 'react';

function App() {
  //khai bao useState cho viec cap nhat result
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState("");
  //cac toan tu su dung
  const opt = ['+', '-', '*', '/', '.'];

  const updateCalc = value =>{
    //Kiem tra va khong cho phep 2 toan tu lien tuc va khi chua nhap thi khong duoc dung toan tu
    if(
      opt.includes(value) && calc === '' || 
      opt.includes(value) && opt.includes(calc.slice(-1))
    ){
      return;
    }
    setCalc(calc + value);
  }

  // Tao button cac so 1 - 9
  const numberCreate = () => {
    const numbers = [];
    for(let i = 1; i < 10; i++)
      {
        numbers.push(
          <button key={i} 
                  onClick={() => updateCalc(i.toString())}>
            {i}
          </button>
        )
      }
      return numbers;
  }

  return (
    <div className="App">
      <div className="calculator">
      {/* Start Display */}
        <div className="display">
          {result ? <span>(0)</span> : ''} 
          <br />
          {calc || "0"}        
        </div>
      {/* End Display */}

      {/* Start Operators */}
        <div className="operators">
          <button>C</button>
          <button onClick={() => updateCalc('+')}>+</button>
          <button onClick={() => updateCalc('-')}>-</button>
          <button onClick={() => updateCalc('*')}>x</button>
          <button onClick={() => updateCalc('/')}>:</button>
        </div>
      {/* End Operators */}

      {/* Start Number */}
        <div className="number">
          { numberCreate() }
          <button onClick={() => updateCalc('0')}>0</button>
          <button onClick={() => updateCalc('.')}>.</button>
          <button>=</button>
        </div>
      {/* End Number */}

      </div>
    </div>
  );
}

export default App;
