import {useState} from 'react';

function App() {
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState("");

  const opt = ['+', '-', '*', '/'];

  const updateCalc = value =>{
    setCalc(calc + value);
  }

  // Tao button cac so 1 - 9
  const numberCreate = () => {
    const numbers = [];
    for(let i = 1; i < 10; i++)
      {
        numbers.push(
          <button key={i}>{i}</button>
        )
      }
      return numbers;
  }

  return (
    <div className="App">
      <div className="calculator">
      {/* Start Display */}
        <div className="display">
          {result? <span>(0)</span> : ""} <br />
          {calc || "0"}        
        </div>
      {/* End Display */}

      {/* Start Operators */}
        <div className="operators">
          <button>C</button>
          <button>+</button>
          <button>-</button>
          <button>x</button>
          <button>:</button>
        </div>
      {/* End Operators */}

      {/* Start Number */}
        <div className="number">
          { numberCreate() }
          <button>0</button>
          <button>.</button>
          <button>=</button>
        </div>
      {/* End Number */}

      </div>
    </div>
  );
}

export default App;
